import Gun from 'gun'
import React, {useState} from 'react'
import produce from 'immer'
import '../public/static/output.css';
import Router from 'next/router'

export const RegisterForm = ({color}) => {
    let gun = Gun()
    let user, email, pass
    const setUser = (value) => {user = value}
    const setEmail = (value) => {email = value}
    const setPass = (value) => {pass = value}
    const handleSubmit = (event) => {
        event.preventDefault()
        gun.get(user).put({username: user, email: email, password: pass})
        console.log(user, pass)
    }
    return (
        <form onSubmit={handleSubmit}>
            <p>
                <InputBox name="Username" set={setUser}  color={color}/>
                <InputBox name="Email"    set={setEmail} color={color}/>
                <InputBox name="Password" set={setPass}  color={color}/>
                <Submit text="Register" color={color}/>
            </p>
        </form>
    )
}

export const LoginForm = ({color}) => {
    let gun = Gun()
    let user, email, pass
    const setUser = (value) => {user = value}
    const setPass = (value) => {pass = value}
    const handleSubmit = (event) => {
        event.preventDefault()
        gun.get(user).on((data, key) => {
            user = data.username
            email = data.email
            pass = data.password
        })
        console.log([user, email, pass])
    }
    return (
        <form onSubmit={handleSubmit}>
            <p>
                <InputBox name="Username" set={setUser} color={color}/>
                <InputBox name="Password" set={setPass} color={color}/>
                <Submit text="Login" color={color}/>
            </p>
        </form>
    )
}

export const StartForm = ({color}) => {
    const handleSubmit = (event) => {
        Router.push('/home')
    }
    const box = `px-2 py-2 ml-8 h-auto w-full rounded`
    const input = `bg-${color}-200 text-base placeholder-blue-700 text-center h-10 w-1/3 rounded-full px-1 border-2 border-${color}-600 float-right mr-1/4`
    const price = `bg-${color}-200 text-base placeholder-blue-700 text-center h-10 w-1/6 rounded-full px-1 border-2 border-${color}-600`
    const text = `h-auto text-2xl overflow-hidden`
    const submit = `p-1 w-32 text-2xl border-2 border-${color}-800 bg-transparent hover:bg-${color}-700 hover:text-white ml-1/4 my-2 rounded-full active:bg-${color}-900 focus:outline-none`
    return (
        <form onSubmit={handleSubmit} className={`bg-${color}-400 rounded pb-4`}> 
            <img src="/static/immobilapp.png" className="p-6"/>
            <p className={`${box} ${text}`}>Place<input type="text" placeholder="City or ZIP" className={input}></input></p>
            <p className={`${box} ${text}`}>Price
                <input type="text" placeholder="From" className={price + " float-right mr-2/6"}></input>
                <input type="text" placeholder="To" className={price + " float-right mr-2/6"}></input>
            </p>
            <p className={`${box} ${text}`}>Radius<input type="text" placeholder="in Km" className={input}></input></p>
            <p className={`${box} ${text}`}>Rooms<input type="text" placeholder="e.g. 1.5"className={input}></input></p>
            <button type="submit"
                className={submit}>
                Search
            </button>
        </form>
    )
}

const InputBox = ({name, set, color}) => {
    const css = `px-2 bg-${color}-400 h-auto w-full rounded`

    return (
        <p  className={css}>

            <TextDisplay text={name}/>
            <Input set={set} color={color}/>
        </p>
    )
}

const TextDisplay = ({text}) => {
    const css = `h-auto text-2xl`
    return (
        <p className={css}>
            {text}
        </p>
    )
}

const Input = ({set, color}) => {
    const css = `bg-${color}-200 text-xl h-10 w-full rounded px-1 border-2 border-${color}-600`
    const handleChange = ({target}) => {set(target.value)}
    return (
        <p>
            <input  type="text"
                    onChange={handleChange}
                    className={css}>
            </input>
        </p>
    )
}

const Submit = ({text, color}) => {
    const css = `p-1 w-32 text-2xl border-2 border-${color}-800 bg-transparent hover:bg-${color}-700 hover:text-white ml-1/4 my-2 rounded active:bg-${color}-900 focus:outline-none`

    return (
        <button type="submit"
            className={css}>
            {text}
        </button>
    )
}
